public abstract class Mobile {
    public abstract String getMessage();
    public abstract void setMessage(String message);
    public abstract String getName();
    public abstract void setName(String name);
    public abstract String getColor();
    public abstract void setColor(String color);
    public abstract String getBrand();
    public abstract void setBrand (String brand);

    public abstract void call();
    public abstract void specs();

    public static void main(String[] args){
        // Story 1
        Mobile iPhone = new iPhone();
        Mobile android = new Android();

        iPhone.setMessage("Call from iPhone");
        iPhone.call();

        android.setMessage("Call from Android");
        android.call();

        //Story 2
        iPhone.setName("Iphone 13");
        iPhone.setColor("Magenta");
        iPhone.setBrand("Iphone");
        iPhone.specs();

        android.setName("Xiaomi 10T Pro");
        android.setColor("Violet");
        android.setBrand("Xiaomi");
        android.specs();

    }
}
